import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-add-api-form',
  templateUrl: './add-api-form.component.html',
  styleUrls: ['./add-api-form.component.css']
})
export class AddApiFormComponent {
  @Input() api = { url: '', header: '', body: '' };
  @Input() index: number = 0; // Provide a default value
  @Output() remove = new EventEmitter<number>();

  removeApi() {
    this.remove.emit(this.index);
  }
}
<div class="card mb-3">
  <div class="card-body">
    <h5 class="card-title">API {{ index + 1 }}</h5>
    <div class="form-group">
      <label for="url-{{index}}">URL</label>
      <input type="text" class="form-control" id="url-{{index}}" [(ngModel)]="api.url" placeholder="Enter API URL">
    </div>
    <div class="form-group">
      <label for="header-{{index}}">Header</label>
      <input type="text" class="form-control" id="header-{{index}}" [(ngModel)]="api.header" placeholder="Enter API Header">
    </div>
    <div class="form-group">
      <label for="body-{{index}}">Body</label>
      <input type="text" class="form-control" id="body-{{index}}" [(ngModel)]="api.body" placeholder="Enter API Body">
    </div>
    <button class="btn btn-danger" (click)="removeApi()">Remove</button>
  </div>
</div>
