import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-api-integration',
  templateUrl: './api-integration.component.html',
  styleUrls: ['./api-integration.component.css']
})
export class ApiIntegrationComponent {
  apis = [{ url: '', header: '', body: '' }];
  responses = [];

  constructor(private http: HttpClient) { }

  addApi() {
    this.apis.push({ url: '', header: '', body: '' });
  }

  removeApi(index: number) {
    this.apis.splice(index, 1);
  }

  runTests() {
    this.http.post('/run-tests', { apis: this.apis }).subscribe((data: any) => {
      this.responses = data.responses;
    });
  }
}
